var n;
var k;


function setSpath(path,Fs,Fe,Ls,Le){

	
	var pathLength=0;

	while(path[pathLength]!=0){
		pathLength++;
	}


	/*first part*/


	if(pathNumArray[ClickPath[0]][0]==path[1] ){
		
		k=Fs.length-1;

		for(var j=0;j<Fs.length;j++)
		{
			finalPath[j]=Fs[k];

			k--;
		}		
	}

	else if(pathNumArray[ClickPath[0]][1]==path[1] ){
             
       	for(var j=0;j<Fe.length;j++)
       	{
       		finalPath[j]=Fe[j];


       	}
	}



 		

              
     //mid part
     k=finalPath.length-1;

    if(pathLength>3){
			    
    	n=1;
    	for(t=0;t<(pathLength-3);t++){
    		 
    		 //console.log("t="+t);
			    for(var i=0;i<pathNumArray.length;i++){   

			    		
			    		
			    	 if(pathNumArray[i][0]==path[n]&&pathNumArray[i][1]==path[n+1]){
										
			    	 				for(var j=0;j<LatLngPath[i].length;j++){

			    	 					finalPath[k]=LatLngPath[i][j];
			    	 					k++;

			    	 				}
					    	 			break;
					    }
					   else if((pathNumArray[i][1]==path[n]&&pathNumArray[i][0]==path[n+1])){
					   			
						   			for(var j=(LatLngPath[i].length-1);j>-1;j--){

				    	 					finalPath[k]=LatLngPath[i][j];
				    	 					k++;

				    	 				}
						    	 			break;
					  	}
			    	 	
			    	 	
			    	 		}
			    	 		n++;
			    	 	}
		 			 		

			 		}
			   
  k=finalPath.length-1;   
//last path

	if(pathNumArray[ClickPath[1]][0]==path[pathLength-2] ){
		
           	for(var j=0;j<Ls.length;j++){
           		finalPath[k]=Ls[j];
           		k++;
           	}

		}
	else if(pathNumArray[ClickPath[1]][1]==path[pathLength-2] ){
		
              	for(var j=(Le.length-1);j>-1;j--){

              		finalPath[k]=Le[j];
              		k++;
              	}

		}

	
 			//console.log(finalPath);
          
    
}
