//function-Dijkstra Algorithmn

        

function DijkstraAlgorithm(s,t){

        
      var IN=9999,N=23,sours,target,LengthOfSpath;
      var m,min,start,d;
      var s,t;

      var sPath=[];
      
      var dist =[];
      var prev=[];
      var selected=[];
      var path=[];

      var i,j;

             /*create cost matrix*/
		
      
        for( i=0;i<N;i++)
        {
            selected[i]=sPath[i]=path[i]=0;
        }
     
        
        sours=s;
        target=t;

            for (i = 1; i < N; i++)
            {
                dist[i] = IN;
                prev[i] = -1;
            }

            start = sours;
            selected[start] = 1;
            dist[start] = 0;
            while (selected[target] == 0)
            {
                min = IN;
                m = 0;
                for (i = 1; i < N; i++)
                {
                    d = dist[start] + cost[start][i];
                    if (d < dist[i] && selected[i] == 0)
                    {
                        dist[i] = d;
                        prev[i] = start;
                    }
                    if (min > dist[i] && selected[i] == 0)
                    {
                        min = dist[i];
                        m = i;
                    }
                }
                start = m;
                selected[start] = 1;
            }
            start = target;
            j = 0;
            i = 0;
            while (start != -1)
            {
               
                // path[j++] = start+65;
                path[j++] = start;
                start = prev[start];
                sPath[i++] = path[j - 1];
           
            }
          
            LengthOfSpath = dist[target];
            
           
            
	var path;

      i = N-1;
            while (sPath[i] == 0)
            {
             
                i--;
            }
           
          
             for (j = i; j >= 0; j--)
            {
                path[i-j]=sPath[j]
            }
          
          
               return path; 
    


  }
